<?php    $videos = array();	if (strtoupper(substr(PHP_OS, 0, 3)) === 'WIN') {		$gb_url = iconv("UTF-8", "gb2312", $_GET["path"]);  	} else {		$gb_url = $_GET["path"];  	}	$myfile = fopen($gb_url, "r") or die("Unable to open file!");		$returnArray = array();	// Output one line until end-of-file	while(!feof($myfile)) {		$time = trim(fgets($myfile));		array_push($returnArray, $time);	}	fclose($myfile);    	$returnJSON = json_encode($returnArray);	echo $returnJSON;?>