<?php    header('Content-type: text/html; charset=utf-8');    echo "<center><br>\n";	    $db = new SQLite3('DatabaseVocabulary.db');	$db->exec('DROP TABLE IF EXISTS temp_word');	$db->exec('DROP TABLE IF EXISTS temp_video');	$db->exec('CREATE TABLE IF NOT EXISTS temp_word (word_id INT NOT NULL, word TEXT NOT NULL, pronunciation TEXT NOT NULL, wordlist_id INT NOT NULL)');    $db->exec('CREATE TABLE IF NOT EXISTS temp_video (movie_name TEXT NOT NULL, file_name TEXT NOT NULL, sentence TEXT NOT NULL)');    $selected = "";	$word_id = htmlspecialchars($_GET["word_id"]);	echo "word_id = $word_id\n";	if ($word_id == 0) {		$db->exec('INSERT INTO temp_word (word_id, word, pronunciation, translation, wordlist_id) VALUES (0, "", "", "", 0);');	} else {		$results = $db->query('SELECT * FROM word where id=' . $word_id);		while ($row = $results->fetchArray()) {			$db->exec('INSERT INTO temp_word (word_id, word, pronunciation, wordlist_id) VALUES (' . $word_id . ', "' . $row['word'] . '", "' . $row['pronunciation'] . '", ' . $row['wordlist_id'] . ');');		}				$results = $db->query('SELECT * FROM word_video INNER JOIN video ON word_video.video_id = video.video_id INNER JOIN word ON word_video.word_id = word.id where word.id='. $word_id);		while ($row = $results->fetchArray()) {			$db->exec('INSERT INTO temp_video (movie_name, file_name, sentence) VALUES ("' . $row['movie_name'] . '", "' . $row['file_name'] . '", "' . $row['sentence'] . '");');			$selected = "?selected=0";		}	}	$db->close();		echo "<script type='text/javascript'> window.location = 'addWord.php" . $selected . "' </script>";?>